import axios from "axios";
import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

import "./assets/css/style.css";
import "@fortawesome/fontawesome-free/css/all.min.css";

const Update = () => {
  const [product, setProduct] = useState({
    name: "",
    price: "",
    quantity: "",
    image: "",
    discount: "",
    description: "",
  });
  const [error, setError] = useState(false);

  const location = useLocation();
  const navigate = useNavigate();

  const productId = location.pathname.split("/")[2];

  const handleChange = (e) => {
    setProduct((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleClick = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`http://localhost:8000/products/${productId}`, product);
      navigate("/");
    } catch (err) {
      console.log(err);
      setError(true);
    }
  };

  return (
    // ... (JSX and HTML)

    <div className="gauche">
      <div className="groupe">
        <label>Product Name</label>
        <input
          type="text"
          autoComplete="off"
          name="name"
          value={product.name} // Set the value to the state value
          onChange={handleChange} // Connect the onChange event to handleChange function
          placeholder="Enter product name"
        />
        <i className="fas fa-box"></i>
      </div>
      <div className="groupe">
        <label>Price</label>
        <input
          type="number" // Use type="number" for numeric fields
          autoComplete="off"
          name="price"
          value={product.price}
          onChange={handleChange}
          placeholder="Enter price"
        />
        <i className="fas fa-money-bill"></i>
      </div>
      <div className="groupe">
        <label>Quantity</label>
        <input
          type="number" // Use type="number" for numeric fields
          autoComplete="off"
          name="quantity"
          value={product.quantity}
          onChange={handleChange}
          placeholder="Enter quantity"
        />
        <i className="fas fa-sort-numeric-up"></i>
      </div>
      {/* ... (other input fields) */}
    </div>

    // ... (other parts of the component)
  );
};

export default Update;
