import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import "./assets/css/style.css";

const Products = () => {
  //create a use statement
    const [products, setProducts] = useState([]);
  
    useEffect(() => {
      const fetchAllProducts = async () => {
        try {
          const res = await axios.get("http://localhost:8000/products");
          console.log(res.data)//specific the response
        } catch (err) {
          console.log(err)
        }
      };
      fetchAllProducts();
    }, [])
  

  return (
    <div className="container2">

        <section id="addProduct">
            <form>
                <h1>Add Product</h1>
                <div class="separation"></div>
                <div class="corps-formulaire">
                    <div class="gauche">
                        <div class="groupe">
                            <label>Product Name</label>
                            <input type="text" autocomplete="off" placeholder="Enter product name" />
                            <i class="fas fa-box"></i>
                        </div>
                        <div class="groupe">
                            <label>Price</label>
                            <input type="text" autocomplete="off" placeholder="Enter price" />
                            <i class="fas fa-money-bill"></i>
                        </div>
                        <div class="groupe">
                            <label>Quantity</label>
                            <input type="text" autocomplete="off" placeholder="Enter quantity" />
                            <i class="fas fa-sort-numeric-up"></i>
                        </div>
                        <div class="groupe">
                            <label>Image URL</label>
                            <input type="text" autocomplete="off" name="image" placeholder="Enter image URL" />
                            <i class="fas fa-image"></i>
                        </div>
                        <div class="groupe">
                            <label>Discount</label>
                            <input type="text" autocomplete="off" name="discount" placeholder="Enter discount" />
                            <i class="fas fa-percent"></i>
                        </div>
                        <div class="groupe">
                            <label>Description</label>
                            <textarea name="description" placeholder="Enter product description"></textarea>
                        </div>
                    </div>

                    <div class="pied-formulaire">
                        <button class="btn">Add Product</button>
                    </div>
                </div>
            </form>
        </section>
    </div>
  );
};

export default Products;
