import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import defaultImage from "./assets/img/quinoa.jpg"; // Import a default image



import "./assets/css/style.css";

const Products = () => {
  // Create a useState hook to store the products data
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchAllProducts = async () => {
      try {
        const res = await axios.get("http://localhost:8000/products");
        console.log(res.data); // Log the response to see if data is fetched properly
        setProducts(res.data); // Set the fetched data to the products state
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllProducts();
  }, []);

  return (
    <section className="products" id="products">
      <h1 className="heading">
        latest <span>products</span>{" "}
      </h1>

      <div className="box-container centered-container" id="productContainer">
        {products.map((product) => (
          <div key={product.id} className="box">
            {product.discount}
            <span className="discount">-{product.discount}%</span>
            <div className="icon">
              <a href="#" className="fas fa-eye"></a>
              <a href="#" className="fas fa-edit"></a>
              <a href="#" className="fas fa-trash"></a>
            </div>

            
            {/* <img src={"C:\Users\BILEL\Documents\Bureau\My Project ( Dev )\ProductCRUD\client\src\pages\assets\img\category-1.png" + product.image} alt={product.name} /> */}
            {/* <img src={"./assets/img/" + product.image} alt={product.image} /> */}
            <img src={`../assets/img/${product.image}`} alt={product.image} onError={(e) => e.target.src = defaultImage} />
            <h3>{product.name}</h3>
            <div className="stars">
              <i className="fas fa-star"></i>
              <i className="fas fa-star"></i>
              <i className="fas fa-star"></i>
              <i className="fas fa-star"></i>
              <i className="fas fa-star-half-alt"></i>
            </div>
            <div className="price">
              $${product.price} <span>${product.price}</span>
            </div>
            <div className="quantity">
              <span>quantity :</span>
              <input type="number" min="1" max="100" value={product.quantity} />
              <span> /kg</span>
            </div>
            <a href="#" className="btn">
              add to cart
            </a>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Products;
